import React from 'react';
import '../index.css';

class Main extends React.Component {
  constructor(props) {
    super(props);
  }

  handleEditAvatarClick = () => {
    document.querySelector('#profileAvatar').classList.add('popup_opened');
  };
  handleEditProfileClick = () => {
    document.querySelector('#editProfile').classList.add('popup_opened');
  };
  handleAddPlaceClick = () => {
    document.querySelector('#addCard').classList.add('popup_opened');
  };

  render() {
    return (
      <main className="content page__content">
        <section className="profile content__profile">
          <div className="profile__card">
            <div className="profile__edit">
              <img className="profile__avatar" src="#" alt="Фото профиля" />
              <button
                onClick={this.handleEditAvatarClick}
                title="Изменить аватар"
                className="profile__avatar-button"
              ></button>
            </div>
            <div className="profile__info">
              <h1 className="profile__title">Жак-Ив Кусто</h1>
              <button
                onClick={this.handleEditProfileClick}
                className="profile__edit-button"
                type="button"
                aria-label="Редактировать"
                data-id="edit-profile"
              ></button>
              <p className="profile__job">Исследователь океана</p>
            </div>
          </div>
          <button
            onClick={this.handleAddPlaceClick}
            className="profile__add-button"
            type="button"
            aria-label="Добавить"
            data-id="addCard"
          ></button>
        </section>
        <section className="places content__places"></section>
      </main>
    );
  }
}
export default Main;
