import React from 'react';
import '../index.css';
import { api } from '../utils/Api';

function Main({ onEditProfile, onAddPlace, onEditAvatar }) {
  const [userName, setUserName] = React.useState({});
  const [userDescription, setUserDescription] = React.useState({});
  const [userAvatar, setUserAvatar] = React.useState({});

  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    Promise.all([api.getProfile(), api.getInitialCards()])
      .then(([user, card]) => {
        setUserName(user.name);
        setUserDescription(user.about);
        setUserAvatar(user.avatar);
        setCards(card);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <section className="content page__content">
      <section className="profile content__profile">
        <div className="profile__card">
          <div className="profile__edit">
            <img
              className="profile__avatar"
              // src={userAvatar}
              style={{ backgroundImage: `url(${userAvatar})` }}
              alt="Фото профиля"
            />
            <button
              onClick={onEditAvatar}
              title="Изменить аватар"
              className="profile__avatar-button"
            ></button>
          </div>
          <div className="profile__info">
            <h1 className="profile__title">{userName}</h1>
            <button
              onClick={onEditProfile}
              className="profile__edit-button"
              type="button"
              aria-label="Редактировать"
              data-id="edit-profile"
            ></button>
            <p className="profile__job">{userDescription}</p>
          </div>
        </div>
        <button
          onClick={onAddPlace}
          className="profile__add-button"
          type="button"
          aria-label="Добавить"
          data-id="addCard"
        ></button>
      </section>
      <section className="places content__places">
        {cards.map((card) => (
          <article class="place places__place">
            <input
              type="image"
              class="place__image"
              src=""
              style={{ backgroundImage: `url(${card.link})` }}
              alt=""
            />
            <button
              class="place__delete"
              type="button"
              aria-label="Удалить"
            ></button>
            <div class="place__item">
              <h2 class="place__title">{card.name}</h2>
              <div class="place__like-area">
                <button
                  class="place__like"
                  type="button"
                  aria-label="Нравится"
                ></button>
                <span class="place__like-counter"></span>
              </div>
            </div>
          </article>
        ))}
      </section>
    </section>
  );
}
export default Main;
